import java.util.Scanner;
public class ForLoopPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		for (int x = 1; x <= 10; x++) {
			for (int y = 1; y <= 10; y++) {
				
				
				System.out.printf("%4d", x * y);
			}
			System.out.println();
			System.out.println();
		}
		
		
		
		
	}

}
